interface LanguageProviderProps {
  children: ReactNode;
}

// Helper function to detect browser language
const detectBrowserLanguage = (): Language => {
  if (typeof window === 'undefined') return 'de'; // SSR fallback
  
  const browserLang = navigator.language || navigator.languages?.[0] || 'de';
  
  // Check if browser language starts with 'en' (en, en-US, en-GB, etc.)
  if (browserLang.toLowerCase().startsWith('en')) {
    return 'en';
  }
  
  // Default to German for all other languages (since this is primarily a German site)
  return 'de';
};

// Helper function to get initial language
const getInitialLanguage = (): Language => {
  if (typeof window === 'undefined') return 'de'; // SSR fallback
  
  try {
    // First, check localStorage for saved preference
    const savedLanguage = localStorage.getItem('volatile-language') as Language;
    if (savedLanguage && (savedLanguage === 'de' || savedLanguage === 'en')) {
      return savedLanguage;
    }
  } catch (error) {
    // localStorage might be disabled or unavailable
    console.warn('localStorage not available, falling back to browser language detection');
  }
  
  // Fallback to browser language detection
  return detectBrowserLanguage();
};

export const LanguageProvider: React.FC<LanguageProviderProps> = ({ children }) => {
  const [language, setLanguage] = useState<Language>('de'); // Start with default
  const [isInitialized, setIsInitialized] = useState(false);

  // Initialize language on mount (client-side only)
  useEffect(() => {
    const initialLanguage = getInitialLanguage();
    setLanguage(initialLanguage);
    setIsInitialized(true);
  }, []);

  // Enhanced setLanguage function that saves to localStorage
  const setLanguageWithPersistence = (lang: Language) => {
    setLanguage(lang);
    if (typeof window !== 'undefined') {
      try {
        localStorage.setItem('volatile-language', lang);
      } catch (error) {
        // localStorage might be disabled or full
        console.warn('Could not save language preference to localStorage:', error);
      }
    }
  };

  const t = (key: string): string => {
    const translation = translations[language] as Record<string, string>;
    return translation[key] || key;
  };

  return (
    <LanguageContext.Provider value={{ language, setLanguage: setLanguageWithPersistence, t }}>
      {children}
    </LanguageContext.Provider>
  );
};

export const useLanguage = (): LanguageContextType => {
  const context = useContext(LanguageContext);
  if (!context) {
    throw new Error('useLanguage must be used within a LanguageProvider');
  }
  return context;
};
